mapper.select:
SELECT IFNULL(SUM(money),0) AS money,days.days AS dateTime FROM ( SELECT @date :=DATE_ADD(@date,INTERVAL+1 DAY) days FROM ( SELECT @date :=DATE_ADD(#{beginTime},INTERVAL-1 DAY) FROM lawyer_area ) tiem
WHERE @date < DATE(#{endTime})
) days LEFT JOIN lawyer_task ON TO_DAYS(lawyer_task.create_time)=TO_DAYS(days.days) and lawyer_task.pid IS NOT NULL AND (lawyer_task.status>0) and lawyer_task.pid = #{pid} and lawyer_task.p_name like concat('%', #{pName}, '%') GROUP BY days.days

SELECT IFNULL(COUNT(task.id) , 0) AS count,days.days AS dateTime FROM ( SELECT @date :=DATE_ADD(@date,INTERVAL+1 DAY) days FROM ( SELECT @date :=DATE_ADD(#{beginTime},INTERVAL-1 DAY) FROM lawyer_area ) tiem
WHERE @date < DATE(#{endTime})
) days LEFT JOIN lawyer_task AS task ON TO_DAYS(task.create_time)=TO_DAYS(days.days) and task.pid IS NOT NULL AND (task.status>0) and task.pid = #{pid} and task.p_name like concat('%', #{pName}, '%') GROUP BY days.days
